package Day4;
public class NumericandAssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=9;
		double f=9.9;
		
		i += 6;	//i=i+6; i=15
		System.out.println("value after addition and assignment= " +i);
		
		i *= 6; //i=i*6; i=90
		System.out.println("value after multipliation and assignment= " +i);
				
		i-=10; //i=i-10; i=80
		System.out.println("value after subtraction and assignment= " +i);
		
		f -= 6.0;	
		System.out.println("value after sub and assignment= " +f);
				
		f /= 6;	
		System.out.println("value after division and assignment= " +f);
		
		}
}



